            <!-- Navigation (Desktop) -->
            <nav class="hidden md:flex space-x-6">
                <a href="#" @click="active = 'home'" :class="active === 'home' ? 'text-[#FF4B3E] font-bold relative' : 'text-gray-600'" class="pb-1">
                    Home
                    <span x-show="active === 'home'" class="absolute left-1/2 -bottom-2 w-2 h-2 bg-[#FF4B3E] rounded-full transform -translate-x-1/2"></span>
                </a>
                <a href="#" @click="active = 'services'" :class="active === 'services' ? 'text-[#FF4B3E] font-bold relative' : 'text-gray-600'" class="pb-1">
                    Services
                    <span x-show="active === 'services'" class="absolute left-1/2 -bottom-2 w-2 h-2 bg-[#FF4B3E] rounded-full transform -translate-x-1/2"></span>
                </a>
                <a href="#" @click="active = 'how'" :class="active === 'how' ? 'text-[#FF4B3E] font-bold relative' : 'text-gray-600'" class="pb-1">
                    How it Works
                    <span x-show="active === 'how'" class="absolute left-1/2 -bottom-2 w-2 h-2 bg-[#FF4B3E] rounded-full transform -translate-x-1/2"></span>
                </a>
            </nav>

            <!-- Mobile Menu Button -->
            <button class="md:hidden text-gray-700 text-3xl focus:outline-none" @click="open = !open">&#9776;</button>

            <!-- Actions (Language & Login) -->
            <div class="hidden md:flex items-center gap-4">
                <select class="border px-2 py-1 rounded-md bg-white text-gray-700">
                    <option>En</option>
                    <option>Fr</option>
                </select>
                <a href="/sign-in" class="px-4 py-2 bg-[#FF4B3E] text-white rounded-md">Log in</a>
            </div>
        </div>

        <!-- Mobile Menu -->
        <div x-show="open" class="md:hidden grid bg-white rounded-md mt-2 p-4 space-y-4 text-center">
            <select class="border px-2 py-1 rounded-md bg-white text-gray-700">
                <option>English</option>
                <option>Francais</option>
            </select>
            <a href="#" @click="active = 'home'" :class="active === 'home' ? 'text-[#FF4B3E] font-bold relative' : 'text-gray-600'" class="pb-1">
                Home
                <span x-show="active === 'home'" class="absolute left-1/2 -bottom-2 w-2 h-2 bg-[#FF4B3E] rounded-full transform -translate-x-1/2"></span>
            </a>
            <a href="#" @click="active = 'services'" :class="active === 'services' ? 'text-[#FF4B3E] font-bold relative' : 'text-gray-600'" class="pb-1">
                Services
                <span x-show="active === 'services'" class="absolute left-1/2 -bottom-2 w-2 h-2 bg-[#FF4B3E] rounded-full transform -translate-x-1/2"></span>
            </a>
            <a href="#" @click="active = 'how'" :class="active === 'how' ? 'text-[#FF4B3E] font-bold relative' : 'text-gray-600'" class="pb-1">
                How it Works
                <span x-show="active === 'how'" class="absolute left-1/2 -bottom-2 w-2 h-2 bg-[#FF4B3E] rounded-full transform -translate-x-1/2"></span>
            </a>
            <a href="/sign-in" class="w-full bg-[#FF4B3E] text-white px-2 py-1 rounded-md mt-2">Log in</a>
            
        </div>
    </header>


    <!-- Hero Section with Carousel -->
    <section class="bg-[#FCF8F3] text-center py-20 relative" x-data="{ activeSlide: 1, slides: ['{{asset('web/assets/images/image 6.png')}}', '{{asset('web/assets/images/image 7.png')}}', '{{asset('web/assets/images/image 4.png')}}'] }">
        <h1 class="text-5xl font-extrabold leading-tight">Elevate Patient Care With Our <br>Precision<span class="text-[#FF4B3E]"> Optical Solutions</span></h1>
        <p class="mt-4 text-gray-600 max-w-3xl mx-auto">
            Trust in our expertise to enhance your hospital’s standard of care, redefining the intersection of vision and healthcare.
        </p>
    
        <!-- Slider Layout -->
        <div class="mt-10 flex justify-center items-center gap-8">
            <!-- Left Image -->
            <img :src="slides[(activeSlide + slides.length - 1) % slides.length]" alt="Glasses Left" class="w-32 opacity-50 transition-all duration-500">
            
            <!-- Center Image -->
            <img :src="slides[activeSlide]" alt="Glasses Center" class="w-32 transition-all duration-500">
            
            <!-- Right Image -->
            <img :src="slides[(activeSlide + 1) % slides.length]" alt="Glasses Right" class="w-32 opacity-50 transition-all duration-500">
        </div>
    
        <!-- Navigation Buttons -->
        <div class="mt-6 flex justify-center items-center gap-4">
            <!-- Left Arrow -->
            <button class="px-1 py-1 border border-gray-400 hover:bg-[#FF4B3E] rounded-full" @click="activeSlide = (activeSlide - 1 + slides.length) % slides.length">
                <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <mask id="mask0_3_309" style="mask-type:alpha" maskUnits="userSpaceOnUse" x="0" y="0" width="14" height="14">
                    <rect x="0.150681" y="0.150681" width="13.1507" height="13.1507" fill="#D9D9D9"/>
                    </mask>
                    <g mask="url(#mask0_3_309)">
                    <path d="M8.91785 12.2055L3.4384 6.72603L8.91785 1.24658L9.89046 2.21918L5.38361 6.72603L9.89046 11.2329L8.91785 12.2055Z" fill="black" fill-opacity="0.3"/>
                    </g>
                </svg>                    
            </button>
    
            <!-- Indicator Dots -->
            <div class="flex gap-2">
                <span :class="activeSlide === 0 ? 'w-2 h-2 bg-black rounded-full' : 'w-2 h-2 bg-gray-400 rounded-full'"></span>
                <span :class="activeSlide === 1 ? 'w-2 h-2 bg-black rounded-full' : 'w-2 h-2 bg-gray-400 rounded-full'"></span>
                <span :class="activeSlide === 2 ? 'w-2 h-2 bg-black rounded-full' : 'w-2 h-2 bg-gray-400 rounded-full'"></span>
            </div>
    
            <!-- Right Arrow -->
            <button class="px-1 py-1 border border-gray-400 hover:bg-[#FF4B3E] rounded-full" @click="activeSlide = (activeSlide + 1) % slides.length">
                <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <mask id="mask0_3_318" style="mask-type:alpha" maskUnits="userSpaceOnUse" x="0" y="0" width="14" height="14">
                    <rect width="13.1507" height="13.1507" transform="matrix(-1 0 0 1 13.8493 0.150681)" fill="#D9D9D9"/>
                    </mask>
                    <g mask="url(#mask0_3_318)">
                    <path d="M5.08214 12.2055L10.5616 6.72603L5.08214 1.24658L4.10954 2.21918L8.61639 6.72603L4.10954 11.2329L5.08214 12.2055Z" fill="#1C1B1F"/>
                    </g>
                </svg>                    
            </button>
        </div>
    </section>
    <section class="bg-white px-24 py-16">
        <div class="max-w-7xl mx-auto flex flex-col md:flex-row items-center gap-16">
            <!-- Image with Gradient -->
            <div class="w-full md:w-1/2 relative">
                <img src="{{asset('web/assets/images/service 1.png')}}" alt="Eye Examination" class="w-full rounded-lg">
                <div class="absolute inset-0 bg-gradient-to-r from-[#FF4B3E] to-transparent opacity-50 rounded-lg"></div>
            </div>
    
           <!-- Sliding Title & Description -->
        <div class="w-full md:w-1/2 text-center md:text-left" x-data="{ slides: [
        { title: 'Sell Optics', image: '{{ asset('web/assets/images/f7_eyeglasses.png')}}', description: 'Trust in our expertise to elevate your hospital’s standard of care, redefining the intersection of vision and healthcare.' },
        { title: 'Enhance Vision', image: '{{ asset('web/assets/images/f7_eyeglasses.png')}}', description: 'Providing precision-crafted optical solutions that set new benchmarks for medical accuracy and patient satisfaction.' },
        { title: 'Medical Precision', image: '{{ asset('web/assets/images/f7_eyeglasses.png')}}', description: 'Delivering high-quality optics designed specifically for medical professionals and healthcare environments.' }
    ], activeIndex: 0 }">
        <div class="flex items-center gap-4">
            <img :src="slides[activeIndex].image" alt="Icon" class="w-10 h-10">
